//! Parlia contracts upgrade.

use block::ExecutedBlock;
use ethereum_types::Address;
use rustc_hex::FromHex;
use spec::CommonParams;
use std::collections::HashMap;
use std::str::FromStr;

#[derive(Copy, Clone, Debug, PartialEq, PartialOrd)]
pub struct UpgradeConfig {
    pub contract_addr: &'static str,
    pub code: &'static str,
}

pub fn upgrade_build_in_system_contract(params: &CommonParams, _block: &mut ExecutedBlock) {
    let number = _block.header.number();
    if number == params.mirror_sync_transition {
        apply_system_contract_upgrade(UPGRADE_CONFIG.get(&params.chain_id).unwrap(), _block);
    }
}

pub fn apply_system_contract_upgrade(upgrade: &Vec<UpgradeConfig>, _block: &mut ExecutedBlock) {
    let state = &mut _block.state;
    for x in upgrade.iter() {
        let addr: Address = Address::from_str(x.contract_addr).unwrap();
        state
            .reset_code(&addr, x.code.from_hex().unwrap())
            .expect("failed to write system contract code");
    }
}

lazy_static! {
    pub static ref UPGRADE_CONFIG: HashMap<u64, Vec<UpgradeConfig>> = [(
        56,
        vec![
            UpgradeConfig {
                contract_addr: "0000000000000000000000000000000000001008",
                code: "",
            },
            UpgradeConfig {
                contract_addr: "0000000000000000000000000000000000001004",
                code: "",
            },
            UpgradeConfig {
                contract_addr: "0000000000000000000000000000000000001005",
                code: "",
            },
        ],
    )]
    .iter()
    .cloned()
    .collect();
}
